$(document).ready(function(){if($("#jadwal-shalat").length){const a="https://api.banghasan.com/",s=`sholat/format/json/kota/kode/${KODE_KOTA}`,l=`sholat/format/json/jadwal/kota/${KODE_KOTA}/tanggal/${TANGGAL}`;try{$.ajax({url:a+s,type:"get",dataType:"json",crossDomain:!0,success:function(a){$("[data-name=kota]").html(a.kota[0].nama).removeClass("shimmer line-short")},error:function(a){$(".line-short").html('<span class="small"><i class="fa fa-exclamation-triangle pr-1"></i> Gagal memuat</span>'),$(".line-short").removeClass("shimmer line-short")}}),$.ajax({url:a+l,type:"get",dataType:"json",crossDomain:!0,success:function(a){$(".shimmer").removeClass("shimmer"),$("[data-name=imsak]").html(`<span class="small">Imsak</span><span>${a.jadwal.data.imsak}</span>`),$("[data-name=subuh]").html(`<span class="small">Subuh</span><span>${a.jadwal.data.subuh}</span>`),$("[data-name=dzuhur]").html(`<span class="small">Dzuhur</span><span>${a.jadwal.data.dzuhur}</span>`),$("[data-name=ashar]").html(`<span class="small">Ashar</span><span>${a.jadwal.data.ashar}</span>`),$("[data-name=maghrib]").html(`<span class="small">Maghrib</span><span>${a.jadwal.data.maghrib}</span>`),$("[data-name=isya]").html(`<span class="small">Isya</span><span>${a.jadwal.data.isya}</span>`)},error:function(a){$(".box-shalat").html('<span class="small"><i class="fa fa-exclamation-triangle pr-1"></i> Gagal memuat</span>'),$(".box-shalat").removeClass("shimmer")}})}catch(a){console.log(a)}}});