$(document).ready(function(){if($("#jadwal-shalat").length){const a="https://api.banghasan.com/",s=`sholat/format/json/kota/kode/${KODE_KOTA}`,t=`sholat/format/json/jadwal/kota/${KODE_KOTA}/tanggal/${TANGGAL}`;$.ajax({url:a+s,type:"get",dataType:"json",crossDomain:!0,success:function(a){$("[data-name=kota]").html(a.kota[0].nama).removeClass("shimmer line-short")}}),$.ajax({url:a+t,type:"get",dataType:"json",crossDomain:!0,success:function(a){$(".shimmer").removeClass("shimmer"),$("[data-name=imsak]").html(`<span class="small text-bold">Imsak</span><span>${a.jadwal.data.imsak}</span>`),$("[data-name=subuh]").html(`<span class="small text-bold">Subuh</span><span>${a.jadwal.data.subuh}</span>`),$("[data-name=dzuhur]").html(`<span class="small text-bold">Dzuhur</span><span>${a.jadwal.data.dzuhur}</span>`),$("[data-name=ashar]").html(`<span class="small text-bold">Ashar</span><span>${a.jadwal.data.ashar}</span>`),$("[data-name=maghrib]").html(`<span class="small text-bold">Maghrib</span><span>${a.jadwal.data.maghrib}</span>`),$("[data-name=isya]").html(`<span class="small text-bold">Isya</span><span>${a.jadwal.data.isya}</span>`)}})}});